import React, { useState } from 'react';
import './FAQ.css'; // CSS for FAQ component

const FAQ = () => {
    const [activeIndex, setActiveIndex] = useState(null);

    const faqs = [
        {
            question: "What precautions should I take when handling algae?",
            answer: "Always wear gloves and protective eyewear when handling algae to avoid skin and eye irritation."
        },
        {
            question: "Can algae be harmful to pets?",
            answer: "Yes, some algae can produce toxins that are harmful to pets if ingested. Keep pets away from areas with algae blooms."
        },
        {
            question: "Is it safe to swim in water with algae?",
            answer: "Avoid swimming in water that is visibly discolored or has a strong odor, as these can be signs of harmful algal blooms."
        },
        {
            question: "How can I prevent algae growth in my pond?",
            answer: "Reduce nutrient runoff into the pond by controlling the use of fertilizers and maintaining vegetation around the pond to absorb excess nutrients."
        },
        {
            question: "What are the signs of harmful algal blooms?",
            answer: "Signs include discolored water, foul smells, and dead fish or other wildlife. If you suspect a harmful algal bloom, avoid contact with the water and report it to local authorities."
        }
    ];

    const handleClick = (index) => {
        setActiveIndex(activeIndex === index ? null : index);
    };

    return (
        <div className="faq-container">
            <h1>FAQs about Algae Precautions</h1>
            {faqs.map((faq, index) => (
                <div key={index} className="faq-item">
                    <div className="faq-question" onClick={() => handleClick(index)}>
                        {faq.question}
                    </div>
                    {activeIndex === index && (
                        <div className="faq-answer">
                            {faq.answer}
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
}

export default FAQ;
